var DateCalendar;
function changeDate(d){
if (d == "") {
d = DateCalendar;
}
var da = d.split(' ');
day = "<span class='dday'>"+da[0]+"</span>";
month = "<span class='dmonth'>"+da[1].slice(0,3)+"</span>";
year = "<span class='dyear'>"+da[2]+"</strong>";
document.write(day+month+year);
DateCalendar = d;
}
